}
.break {
    flex-basis: 100%;
    height: 0;
  }

.down-border {
        border-right: 2px solid black;
        border-top: 1px solid black;
        width:130px;
        height:30px;

}
.up-border {
            border-right: 2px solid black;
            border-top: 2px solid black;
