    {% endfor %}

    {% set default_unit_of_measure = [] %}
    {% for char in row.default_unit_of_measure | string %}
    <span style="display: none;">{{ default_unit_of_measure.append(numbers.get(char|string, char)) }}</span> 
    {% endfor %}

    {% set qty = [] %}
    {% for char in row.qty | string %}
    <span style="display: none;">{{ qty.append(numbers.get(char|string, char)) }}</span> 
    {% endfor %}

    {% set rate = [] %}
