    <span style="display: none;">{{ rate.append(numbers.get(char|string, char)) }}</span> 
    {% endfor %}

    {% set brand = [] %}
    {% for char in row.brand | string %}
    <span style="display: none;">{{ brand.append(numbers.get(char|string, char)) }}</span> 
    {% endfor %}

    
    <td>{{idx|join("")}}</td>
    
    <td>{{item_name|join("")}}</td>
