        <span style="display: none;">{{ cylinder_count.append(numbers.get(char|string, char)) }}</span> 
        {% endfor %}
        <p>  {{ cylinder_count|join("") }} سلندر</p>
    
 
        </div>  
        <div class="middle-column">
                                                  {%if doc.vehicle_style and doc.vehicle_style != "0" %}
                        <p>{{doc.vehicle_style}}</p>
            {% else %}
            <p>-------</p>
            {% endif %}
            {% set cylinder_count = [] %}
            {% for char in doc.chassis_no | string %}
            <span style="display: none;">{{ cylinder_count.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            <p> {{ cylinder_count|join("") }}</p>
            {% set cylinder_count = [] %}
            {% for char in doc.motor_no | string %}
            <span style="display: none;">{{ cylinder_count.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            <p> {{ cylinder_count|join("") }}</p>
            {% set cylinder_count = [] %}
            {% for char in doc.fuel_type | string %}
            <span style="display: none;">{{ cylinder_count.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            <p>{{ cylinder_count|join("") }}</p>
        
        </div>
        <div class="left-column">
            {% set cylinder_count = [] %}
            {% for char in doc.vehicle_model | string %}
            <span style="display: none;">{{ cylinder_count.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            <p> {{ cylinder_count|join("") }}</p>
            <p> {{doc.vehicle_shape}} </p>

            {% set year = (doc.to_date|string).split('-') %}
            {% set litre_capacity = [] %}
            {% for char in '%0.0f'| format(doc.litre_capacity|float) | string %}
            <span style="display: none;">{{ litre_capacity.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            {% set litre_capacity1 = [] %}
            {% for char in year[0] | string %}
            <span style="display: none;">{{ litre_capacity1.append(numbers.get(char|string, char)) }}</span> 
            {% endfor %}
            <p class="twophrase"> <span> {{ litre_capacity|join("") }}  سم</span> ٣ الفحص  {{ litre_capacity1|join("") }}</p>
            <p> {{doc.vehicle_color}}</p>

        </div> 
    <div class="break"></div> <!-- break -->
        {% if doc.private_no and doc.private_no != "0" %}
