    justify-content: flex-end;
    margin-bottom:-20px;


}
.sec-two {

        display: flex;
    justify-content: center;
    flex-direction:row;


}
.sec-three {
    border: 2px solid black;
    margin-top:40px;
    display: flex;
    justify-content: space-between;
    flex-direction:row;
      margin: 5px auto;
      width:90%;


}
