html:
{% set user = frappe.db.get_value("User", frappe.session.user, ["full_name"])%}
{% set numbers = ({"0":"٠","1":"١","2": "٢", "3":"٣", "4":"٤", "5":"٥", "6":"٦", "7":"٧", "8":"٨", "9":"٩"}) %}

<div style="display:flex; justify-content:space-between; margin-left:20px;margin-top:10px;margin-bottom:-60px;">
    <div>
        <div style="display:flex; flex-direction:column; align-items:center; margin-right:20px;">
            <div>
                <img style="width:40px; height:40px; margin-top:0px;" src="/files/markabat logoce3b5ed30c07.png">
            </div>
            <div>
                وزارة الداخلية
            </div>
            <div>
                الادارة العامه لمركبات الشرطه
            </div>

        </div>

    </div>
    <div style="border:2px solid black; height:fit-content; width:fit-content; text-align:center; font-weight:bold;">
        تاريخ الطباعة
        <br>
        {% set print_date = frappe.utils.today() %}

        {% set date2 = [] %}
        {% for char in print_date | string %}
        <span style="display: none;">{{ date2.append(numbers.get(char|string, char)) }}</span>
        {% endfor %}

        {% set print_date = date2|join("") %}
        {{ print_date.split("-")[2] }} / {{ print_date.split("-")[1] }} / {{ print_date.split("-")[0] }}
        <br>
        الطباعة بواسطة
        <br>
        {{user}}
    </div>

</div>

<div class="sec-two">
    <div class="title">
        <p class="p-tag"> إذن إصلاح داخلي</p>
    </div>
</div>
<div style="text-align: center;">
    {% if doc.renew_ezn == 1 %}
    <p><b>تم تجديد الإذن نظراً لتغيير السنة المالية</b></p>
    {% endif %}
    {% set print_templete1 = [] %}
    {% for char in doc.print_templete1 | string %}
    <span style="display: none;">{{ print_templete1.append(numbers.get(char|string, char)) }}</span>
    {% endfor %}
    <!-- {% set print_internal_ezn_count = doc.print_internal_ezn_count + 1 %} -->
    {% set print_internal_ezn_count = frappe.db.count("Maintenance Print Logs", {"parent":doc.name, "format_name": "كشف
    عهدة", "format_no": doc.print_templete1}) + 1 %}
    {% set print_count = [] %}
    {% for char in print_internal_ezn_count | string %}
    <span style="display: none;">{{ print_count.append(numbers.get(char|string, char)) }}</span>
    {% endfor %}
    <p> نموذج رقم {{ print_templete1|join("") }} - طابعة رقم {{ print_count|join("") }}</p>
</div>
{% set exchange_allowance, litre_capacity, fuel_type = frappe.db.get_value("Vehicles", doc.vehicles,
["exchange_allowance", "litre_capacity", "fuel_type"]) %}

<div class="header-box">
    <div class="header-img">
        <img src="/files/markabat logoce3b5ed30c07.png" alt="">
    </div>
    <div class="sec-three">
        <div>
            {% set vehicle_no = [] %}
            {% for char in doc.vehicle_no | string %}
            <span style="display: none;">{{ vehicle_no.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p style="background-color: lightgray;"> رقم الشرطة: {{ vehicle_no|join("") }}</p>
            <p> الشكل: {{doc.vehicle_shape}}</p>
            <p> نوع الحيازة: {{doc.possession_type}}</p>
            <p style="background-color: lightgray;"> الجهه التابع لها : {{doc.entity_name}} </p>
            {% if doc.maintenance_entity %}
            <p style="background-color: lightgray;"> جهة الصيانة : {{doc.maintenance_entity}} </p>

            {% else %}
            <p> جهة الصيانة : - </p>

            {% endif %}

            {% set entity_date = frappe.db.get_value("Vehicles", doc.vehicles, ["entity_date"]) %}
            {% if entity_date %}
            {% set date2 = [] %}
            {% for char in entity_date | string %}
            <span style="display: none;">{{ date2.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}

            {% set entity_date = date2|join("") %}
            <p style="background-color: lightgray;"> تاريخ تخصيص الجهة : {{ entity_date.split("-")[2] }} / {{
                entity_date.split("-")[1] }} / {{ entity_date.split("-")[0] }} </p>
            {% else %}
            <p> تاريخ تخصيص الجهة : - </p>
            {% endif %}

        </div>
        <div>
            {% set chassis_no = [] %}
            {% for char in doc.chassis_no | string %}
            <span style="display: none;">{{ chassis_no.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> رقم الشاسيه : {{chassis_no|join("")}} </p>
            <p style="background-color: lightgray;"> مجموعة الشكل : {{doc.group_shape}} </p>
            {% set vehicle_model = [] %}
            {% for char in doc.vehicle_model | string %}
            <span style="display: none;">{{ vehicle_model.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> الموديل : {{vehicle_model|join("")}} </p>
            {% set litre_capacity1 = [] %}
            {% for char in litre_capacity | string %}
            <span style="display: none;">{{ litre_capacity1.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> السعة اللترية: {{litre_capacity1|join("")}} </p>
            {% set ezn_no = [] %}
            {% for char in doc.ezn_no | string %}
            <span style="display: none;">{{ ezn_no.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> رقم إذن اللإصلاح : {{ezn_no|join("")}}</p>
            <p> مخصص الصرف: {{exchange_allowance}}</p>

        </div>
        <div>
            <p style="background-color: lightgray;"> الماركه / الطراز : {{doc.vehicle_style}} / {{doc.vehicle_brand}}
            </p>
            {% set motor_no = [] %}
            {% for char in doc.motor_no | string %}
            <span style="display: none;">{{ motor_no.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> رقم الموتور : {{motor_no|join("")}}</p>
            {% set possession_date = [] %}
            {% for char in doc.possession_date | string %}
            <span style="display: none;">{{ possession_date.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            {% set possession_date = possession_date|join("") %}
            <p> تاريخ الحيازة : {{possession_date.split("-")[2]}} - {{possession_date.split("-")[1]}} -
                {{possession_date.split("-")[0]}}</p>
            {% set fuel_type = [] %}
            {% for char in doc.fuel_type | string %}
            <span style="display: none;">{{ fuel_type.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <p> نوع الوقود : {{fuel_type|join("")}}</p>
            {% set date = [] %}
            {% for char in doc.ezn_date | string %}
            <span style="display: none;">{{ date.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            {% set date = date|join("") %}

            <p> تاريخ إذن اللإصلاح : {{date.split("-")[2]}} - {{date.split("-")[1]}} - {{date.split("-")[0]}}</p>
        </div>
    </div>


</div>
<div class="sec-four">

    <table class="table-border">
        <thead>
            <tr>
                <th rowspan="2">كود القطعة</th>
                <th rowspan="2">تاريخ إخر صرف</th>
                <!-- <th rowspan="2">تاريخ إخر إصلاح</th> -->
                <!-- <th rowspan="2">وصف القطعة</th> -->
                <th colspan="2" rowspan="2">قطعة الغيــــار</th>
                <th rowspan="2">المنشأ</th>
                <th rowspan="2">السعر</th>
                <th rowspan="2">الكمية</th>
                <th rowspan="2">الوحدة</th>
                <th rowspan="2">الصلاحية<br>(%)</th>
                <th rowspan="2">متوفر</th>
                <th rowspan="2">مخزن</th>
                <th colspan="4">كميات الإجراء المطلوب</th>
                <th rowspan="2">مهندس<br>ورشة</th>
            </tr>

            <tr class="writing-mode:vertical-rl;">
                <th>صرف</th>
                <th>مشتروات</th>
                <th>أ.خارجى</th>
                <th>الوحدة</th>
            </tr>
        </thead>

        <tbody>
            {% set counter = doc.kashf_ohda_item | length%}
            {%- for row in doc.kashf_ohda_item -%}
            {% if row.namozag_no == doc.print_templete1 %}
            {% set actual_qty = frappe.db.get_value("Bin", {"warehouse": frappe.db.get_value("Item Default", {"parent":
            row.item_code, "idx":1, "parenttype": "Item"}, ["default_warehouse"]), "item_code": row.item_code},
            ["actual_qty"])%}
            <!-- set background color if row.shadow == 1 -->
            {% if row.shadow == 1 %}
            <tr style="background-color: lightgray;">
                {% else %}
            <tr>
                {% endif %}
                {% set item_code = [] %}
                {% for char in row.item_code | string %}
                <span style="display: none;">{{ item_code.append(numbers.get(char|string, char)) }}</span>
                {% endfor %}
                <td>{{item_code|join("")}}</td>
                {% if row.last_issue_detail %}
                {% set last_issue_detail = [] %}
                {% for char in row.last_issue_detail | string %}
                <span style="display: none;">{{ last_issue_detail.append(numbers.get(char|string, char)) }}</span>
                {% endfor %}

                {% set last_issue_detail = last_issue_detail|join("") %}
                {% if last_issue_detail.split("-")|length > 1 %}
                <td>{{ last_issue_detail.split("-")[2] }} / {{ last_issue_detail.split("-")[1] }} / {{
                    last_issue_detail.split("-")[0] }}</td>
                {% else %}
                <td> {{ last_issue_detail }}</td>
                {% endif %}

                {% else %}
                <td>لم يسبق</td>
                {% endif %}

                <td colspan="2">{{row.item_name}} </td>
                {%if row.brand %}

                <td>{{row.brand}}</td>
                {% else %}
                <td> - </td>
                {% endif%}
