css:
@media print{@page {
   
      size: A4 landscape;
   

    
}}

* {
    margin: 0;
    padding: 0;
  }

.print-format {
    padding: 0in;
    font-family: "Times New Roman", Times, serif;
}
.sec-one {
        display: flex;
    justify-content: flex-end;
    margin-bottom:-20px;


}
.sec-two {

        display: flex;
    justify-content: center;
    flex-direction:row;


}
.sec-three {
    border: 2px solid black;
    margin-top:40px;
    display: flex;
    justify-content: space-between;
    flex-direction:row;
      margin: 5px auto;
      width:90%;


}
.inner-sec-two {
            display: flex;
    justify-content: space-between;

}
.title {
          border: 2px solid black;
      width:30%;
}
.p-tag {
    text-align:center;
}
.print-format p{
    font-weight:bold;
    margin:0px 5px 0px 0px;

}
.up {
    margin-left:50px!important; 
}
.work-sz {
    margin-right:70px!important; 
    text-align:center;
}
.work-s {
    margin-right:100px!important; 
    font-weight:bold;
    font-size: large;
}
.work-r {
        margin-left:300px!important; 

}
.blank_row{
    height: 22px !important; /* overwrites any other rules */
    background-color: #FFFFFF;
}
.break {
    flex-basis: 100%;
    height: 0;
  }
  

table, th, td {
  border: 2px solid black;
  border-collapse: collapse;
}
.print-format td, .print-format th {
    vertical-align: top !important;
    padding:2px !important;
}
.sec-four {
margin-top:6px;
}
.table-border {
    margin-top:30px;
    margin: auto;
    width: 90%;
    padding: 10px;
}
.right {
    text-align:left;
}

.sec-five{
    margin-top:40px;
    display: flex;
    justify-content: space-between;
    flex-direction:row;
    flex-wrap: wrap;
      margin: 5px auto;
      width:90%;
}
.after-table {
        margin-top:40px;
    display: flex;
    justify-content: space-between;
    flex-direction:row;
    flex-wrap: wrap;
      margin: 5px auto;
      width:90%;
}
.landscape {
    display:flex;
    
}
.print-format th {
    color:black;
    font-weight:bold;
    background-color:lightgray;
}

