        {% set print_date = frappe.utils.today() %}      {% set date2 = [] %}   {% for char in print_date | string %}  <span style="display: none;">{{ date2.append(numbers.get(char|string, char)) }}</span>    {% endfor %}    {% set print_date = date2|join("") %}   {{ print_date.split("-")[2] }} / {{ print_date.split("-")[1] }} / {{ print_date.split("-")[0] }}   
      <br>
      الطباعة بواسطة
      <br>
      {{user}}
      </div>
      
     </div>
    
        <div class="sec-one">
                <div class="title-1">
                  <p class="up">بدل إستمارة ٢م.ح</p>
                                    <div style="border: 2px solid black; margin-left:20px ; text-align: center; background-color: lightgray; opacity: 0%;">
                    صوره طبق الأصل
                </div>
                </div>
        </div>
        
        <div class="sec-two">
