              {% set print_count = [] %}
              {% for char in print_external_ezn_count | string %}
             <span style="display: none;">{{ print_count.append(numbers.get(char|string, char)) }}</span> 
              {% endfor %}
              <p class="up" style="text-align:center;"> نموذج رقم {{ print_templete2|join("") }}  -  طابعة رقم {{ print_count|join("") }}</p>
            </div>
         </div>
         
    
         <div class="sec-three">
             <div>
                  {% set vehicle_no = [] %}
                  {% for char in doc.vehicle_no | string %}
                  <span style="display: none;">{{ vehicle_no.append(numbers.get(char|string, char)) }}</span> 
                  {% endfor %}
