        {% endfor %}
        {% set date1 = date|join("") %}
        
         <p style="font-size: 10px;margin-left: 6px;"> تاريخ إذن اللإصلاح : {{ date1.split("-")[2] }} / {{ date1.split("-")[1] }} / {{ date1.split("-")[0] }}</p>
