{% set numbers = ({"0":"٠","1":"١","2": "٢", "3":"٣", "4":"٤", "5":"٥", "6":"٦", "7":"٧", "8":"٨", "9":"٩"}) %}
{% set months = ["يناير", "فبراير", "مارس", "ابريل", "مايو", "يونيو", "يوليو","أغسطس","سبتمبر","أكتوبر","نوفمبر",
"ديسمبر"]%}
{% set postingDate = doc.from_date |str %}
{% set cur_month = postingDate.split("-")[1] %}
{% set month_idx = cur_month.lstrip("0") |int -1 %}
{% set curYear = postingDate.split("-")[0] %}
{% set yearPrint = "" %}
{% set data = frappe.db.sql(
"""

SELECT vehicles.vehicle_no vehicle_no, vehicles.vehicle_shape shape, vehicles.vehicle_brand brand,
vehicles.vehicle_status as vehicle_status, vehicles.vehicle_style style , vehicles.name as name,
vehicles.exchange_allowance as exchange_allowance, 'Vehicles' as document_type, 'غير ملحقة' as attached,
(
SELECT
`tabVehicle Status Logs`.date as date
FROM `tabVehicle Status Logs`
WHERE `tabVehicle Status Logs`.parent = vehicles.name
ORDER BY `tabVehicle Status Logs`.date DESC LIMIT 1
) as remarks,
fuel_type.order_no as order_no,
vehicles.oil_type as oil_type
FROM `tabVehicles` vehicles
JOIN `tabFuel Type` fuel_type on fuel_type.name = vehicles.fuel_type
WHERE vehicles.vehicle_status IN ('عاطلة','صالحة', 'تحت التخريد')
AND (vehicles.entity_name = '{0}' and (vehicles.attached_entity is null or vehicles.attached_entity = ''))

UNION

SELECT vehicles.vehicle_no vehicle_no, vehicles.vehicle_shape shape, vehicles.vehicle_brand brand,
vehicles.vehicle_status as vehicle_status, vehicles.vehicle_style style , vehicles.name as name,
vehicles.exchange_allowance as exchange_allowance, 'Vehicles' as document_type, 'ملحقة' as attached,
(
SELECT
`tabVehicle Status Logs`.date as date
FROM `tabVehicle Status Logs`
WHERE `tabVehicle Status Logs`.parent = vehicles.name
ORDER BY `tabVehicle Status Logs`.date DESC LIMIT 1
) as remarks,
fuel_type.order_no as order_no,
vehicles.oil_type as oil_type
FROM `tabVehicles` vehicles
JOIN `tabFuel Type` fuel_type on fuel_type.name = vehicles.fuel_type
WHERE vehicles.vehicle_status IN ('عاطلة','صالحة', 'تحت التخريد')
AND ((vehicles.entity_name != '{0}' and vehicles.attached_entity = '{0}'))

UNION

SELECT boats.boat_no as vehicle_no, boats.body_type as shape, boats.boat_brand as brand,
boats.boat_validity as vehicle_status, boats.boat_style as style , boats.name as name,
'لوحة وسوائل' as exchange_allowance, 'Boats' as document_type, 'غير ملحقة' as attached,
' ' as remarks,
fuel_type.order_no as order_no,
boats.fuel_voucher as oil_type

FROM `tabBoats` boats
JOIN `tabFuel Type` fuel_type on fuel_type.name = boats.fuel_type
WHERE boats.boat_validity IN ('عاطلة','صالحة', 'تحت التخريد')
AND boats.entity_name = '{0}'
order by order_no, shape, brand, style
""".format(doc.entity), as_dict=1) %}
{% set counter = namespace(value=1) %}

{% for count in range((data|length / 32) | int +1) %}

{% set user = frappe.db.get_value("User", frappe.session.user, ["full_name"])%}
<div style="display:flex; justify-content:space-between; margin-left:20px;margin-right:20px;">
    <div style="display:flex; flex-direction:column; align-items:center;margin-top:35px;">
        <div>
            <img style="width:40px; height:40px;" src="/files/markabat logoce3b5ed30c07.png">
        </div>
        <div>
            وزارة الداخلية
        </div>
        <div>
            الادارة العامه لمركبات الشرطه
        </div>
        <div>
        </div>
    </div>
    {% set entity_code = frappe.db.get_value("Entity", doc.entity, ["note"]) %}

    <div class="header" style="margin-top:35px;">
        <div style="display: flex; justify-content:center; ">
            <div style="border:2px solid black; background-color:lightgray;">
                {% set current_year = [] %}
                {% for char in curYear | string %}
                <span style="display: none;">{{ current_year.append(numbers.get(char|string, char)) }}</span>
                {% endfor %}
                <h3 style="font-family:Cairo; margin: 0px 20px"> بيان عهدة {{ doc.entity}} من
                    {{months[month_idx]}} {{ current_year|join("") }}</h3>
            </div>
            {% set doc_entity_code = [] %}
            {% for char in entity_code | string %}
            <span style="display: none;">{{ doc_entity_code.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
        </div>
        {% if entity_code %}
        <h3> كود الجهة {{doc_entity_code|join("")}}</h3>
        {% endif%}
    </div>

    <div
        style="border:2px solid black; height:fit-content; width:fit-content; text-align:center; font-weight:bold;margin-top:35px;">
        تاريخ الطباعة
        <br>
        {% set print_date = frappe.utils.today() %}

        {% set date2 = [] %}
        {% for char in print_date | string %}
        <span style="display: none;">{{ date2.append(numbers.get(char|string, char)) }}</span>
        {% endfor %}

        {% set print_date = date2|join("") %}
        {{ print_date.split("-")[2] }} / {{ print_date.split("-")[1] }} / {{ print_date.split("-")[0] }}
        <br>
        الطباعة بواسطة
        <br>
        {{user}}
    </div>

</div>
<div style="display: flex; justify-content:center;">
    <div class="table-bordered-flex" style="margin-top:20px;">
        <div style="display: flex; flex-direction:column; border-right:2px solid black;">
            <div class="border-bottoms header-background">م</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% set index = [] %}
            {% for char in counter.value | string %}
            <span style="display: none;">{{ index.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <div class="border-bottoms">{{ index|join("") }}</div>
            {% set counter.value = counter.value + 1 %}
            {% endfor %}
        </div>
        <div class="borders"></div>

        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background vehicle">رقم المركبة</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% set vehicle_no = [] %}
            {% for char in row.vehicle_no | string %}
            <span style="display: none;">{{ vehicle_no.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <div class="border-bottoms">{{ vehicle_no|join("") }}</div>

            {% endfor %}
        </div>
        <div class="borders"></div>

        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background">الشـــكل</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.shape %}
            {% set shape = [] %}
            {% for char in row.shape[:16] | string %}
            <span style="display: none;">{{ shape.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}
            <div class="border-bottoms ">{{ shape|join("") }}</div>
            {% else %}
            <div class="border-bottoms">-</div>
            {% endif %}
            {% endfor %}
        </div>
        <div class="borders"></div>

        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background">الماركة</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.brand %}
            <div class="border-bottoms ">{{row.brand}}</div>
            {% else %}
            <div class="border-bottoms">-</div>
            {% endif %}
            {% endfor %}
        </div>
        <div class="borders"></div>

        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background">الــــــــــطراز</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.style %}
            <div class="border-bottoms ">{{row.style}}</div>
            {% else %}
            <div class="border-bottoms">-</div>
            {% endif %}
            {% endfor %}
        </div>
        <div class="borders"></div>
        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background voucher">فئة البون</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.oil_type %}
            <div class="border-bottoms ">{{row.oil_type[:22]}}</div>
            {% else %}
            <div class="border-bottoms">-</div>
            {% endif %}
            {% endfor %}
        </div>
        <div class="borders"></div>

        <div style="display: flex; flex-direction:column;">
            <div class="border-bottoms header-background change">متغيرات الشهر </div>


            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.vehicle_status in ['عاطلة', 'تحت التخريد'] %}
            <div class="border-bottoms ">عاطلة</div>
            {% else %}
            <div class="border-bottoms"><br></div>
            {% endif %}
            {% endfor %}
        </div>
        <div class="borders"></div>
        <div style="display: flex; flex-direction:column; border-left:2px solid black;">
            <div class="border-bottoms left-border header-background">الملحوظات</div>
            {% for row in data[count * 32:(count * 32) + 32] %}
            {% if row.exchange_allowance in ["لوحة فقط", "لوحة وخدمة كاملة فقط"] %}
            <div class="border-bottoms left-border">لوحة فقط</div>
            {% elif row.vehicle_status == "عاطلة" %}
            {% set remarks = [] %}
            {% for char in row.remarks | string %}
            <span style="display: none;">{{ remarks.append(numbers.get(char|string, char)) }}</span>
            {% endfor %}

            {% set print_date = remarks|join("") %}
            {% if row.remarks != " " %}
